import React, { Component } from 'react';
import NewsItem from './NewsItem';

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false
    };
  }

  async componentDidMount() {
    this.setState({ loading: true });
    let url = "https://newsapi.org/v2/everything?q=everything&from=2024-07-03&to=2024-07-03&sortBy=popularity&apiKey=0cdc1ee3184c44239eb1da21f4499707";
    try {
      let data = await fetch(url);
      let personData = await data.json();
      console.log("API Response:", personData); // Debug: Log the API response
      this.setState({ articles: personData.articles, loading: false });
    } catch (error) {
      console.error("Error fetching data:", error);
      this.setState({ loading: false });
    }
  }
  handleClickprev= async ()=>{
    
    console.log("next");
    if(this.state.page+1>Math.ceil(this.state.totalResults/20)){

    }
    this.setState({ loading: true });
    let url = `https://newsapi.org/v2/everything?q=everything&from=2024-07-03&to=2024-07-03&sortBy=popularity&apiKey=0cdc1ee3184c44239eb1da21f4499707&page =${this.state.page+1}&pageSize=20`;
    
      let data = await fetch(url);
      let personData = await data.json();
      console.log("API Response:", personData); // Debug: Log the API response
      this.setState({ articles: personData.articles, loading: false });
    
    
    this.setState({
      page:this.state.page+1,
      articles:personData.articles

    })
  }
  render() {
    return (
      <div>
        <br />
        <div className="container my-3">
          <h1 className="text-center">Daily News - Top Headlines</h1>
          <br></br>
          <div className="row">
            {this.state.loading ? (
              <p>Loading...</p>
            ) : (
              this.state.articles && this.state.articles.length > 0 ? (
                this.state.articles.map((element) => {
                  return (
                    <div className="col-md-4" key={element.url}>
                      <NewsItem 
                        title={element.title ? element.title : ""} 
                        description={element.description ? element.description : ""} 
                        imageUrl={element.urlToImage} // Correct property name
                        newsUrl={element.url} // Correct property name
                      />
                    </div>
                  );
                })
              ) : (
                <p>No articles found</p> // Show message if no articles are found
              )
            )}
          </div>
        </div>
        <div className="container d-flex justify-content-between">
        <button disabled = {this.state.page<=1} type="button" class="btn btn-dark" onClick={this.handleClickprev}> &#8249;  Previous</button>
        <button type="button" class="btn btn-dark" onClick={this.handleClicknext}>Next  &#8250;</button>
        </div>
      </div>
    );
  }
}

export default News;
