 import React, { Component,useState } from 'react';
 
import NewsItem from './NewsItem';
export class News extends Component {
  constructor(){
    super();
    this.state = {
      articles:[],
      loading:false
    }
  }
  async componentDidMount(){
    let url = "https://newsapi.org/v2/everything?q=&from=2024-07-03&to=2024-07-03&sortBy=popularity&apiKey=0cdc1ee3184c44239eb1da21f4499707";
    let data = await fetch(url);
    let personData =  await data.json();
    this.setState({articles:personData.articles});
  }
  render() {
    return (
      
      <div>
        <br></br>
      <div className="container my-3">
      <h2>NewsMonkey - Top Headlines</h2>
      <div className="row">
    {this.state.articles.map((element)=>{
      return   <div className="col-md-4" key = {element.url}>
      <NewsItem title = {element.title?element.title:""} description = {element.description?element.description:""} imageUrl = {element.imageUrl} newsUrl  ={newsUrl}></NewsItem>
      </div>
    })}
        </div>
       
      </div>
      </div>
    )
  }
 }


export default News
